void insertRecord(QSqlQuery &q, int id, const QString &name, const QString &descr,
                  const QString &symbol, const QString &power, const QString &color)
{
    q.addBindValue(id);
    q.addBindValue(name);
    q.addBindValue(descr);
    q.addBindValue(symbol);
    q.addBindValue(power);
    q.addBindValue(color);
    q.exec();
}

void updateDescription(QSqlQuery &q, int id, const QString &name, const QString &descr,
                       const QString &symbol, const QString &power, const QString &color)
{
    q.addBindValue(name);
    q.addBindValue(descr);
    q.addBindValue(symbol);
    q.addBindValue(power);
    q.addBindValue(color);
    q.addBindValue(id);
    q.exec();
}

