    QSqlError openDB(const QString &fileName, const QString &connectionName)
    {
        QSqlDatabase db = QSqlDatabase::addDatabase("QSQLITE", connectionName);
        db.setDatabaseName(fileName);

        if (!db.open())
            return db.lastError();
        return QSqlError();
    }

    QSqlError createDB(QSqlDatabase db)
    {
        QProgressDialog progress(QT_TR_NOOP("Создание базы данных описания лет"), "", 1, 144);
        progress.setWindowTitle(QT_TR_NOOP("База данных описания лет"));
        progress.setCancelButton(0);
        progress.show();

        QSqlQuery q(db);
        if (!q.exec(QString("CREATE TABLE opisanie_let (id integer primary key, name text, descr text, symbol text, power text, color text)")))
            return q.lastError();

        for (int i = 1; i <= 144; ++i) {
            if (!q.prepare(QString("INSERT INTO opisanie_let (id, name, descr, symbol, power, color) VALUES(?, ?, ?, ?, ?, ?)")))
                return q.lastError();
            insertRecord(q, i, "", "", "", "", "");
            progress.setValue(i);
        }

        return QSqlError();
    }

